import './App.css';
import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hora: '00:00:00',
    };
  }

  componentDidMount() {
    setInterval(() => {
      return this.state.hora + 1;
    }, 1000);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>Hi there {this.state.hora}</div>
        </header>
      </div>
    );
  }
}

export default App;
