import React, { Component } from 'react';

class Membro extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nome: props.nome,
    };
    this.novoNome = this.novoNome.binf(this);
  }

  novoNome() {
    let state = this.state;
    state.name = 'Ricardo';
    this.setState(state);
  }
  render() {
    return (
      <div>
        <div>Membros</div>
        <h2>Bem-vindo(a) {this.state.nome}</h2>
        <button onClick={this.novoNome}>Entrar como Ricardo</button>
        <button>Sair</button>
      </div>
    );
  }
}

export default Membro;
