import './App.css';
import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nome: '',
      email: '',
      senha: '',
    };
  }

  chnageInput(e) {
    let input = e.target.value;

    console.log(input);
  }

  render() {
    return (
      <div>
        <h1>Novo Usuario</h1>

        <form>
          <input
            type="text"
            value={this.state.nome}
            onChange={this.chnageInput}
          />
        </form>
      </div>
    );
  }
}

export default App;
