      nome: '',
      email: '',
      senha: '',
    };
  }

  render() {
    return (
      <div>
        <h1>Novo Usuario</h1>

        <form>
          <input type="text" value={this.state.nome} onChange={chnageInput} />
        </form>
      </div>
    );
  }
}

export default App;
