import './App.css';
import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: 'test@test.com',
      senha: '11111',
    };
    this.trocarEmail = this.trocarEmail.bind(this);
    // this.state.senha = this.state.senha.bind(this);
  }

  trocarEmail(e) {
    let userInput = e.target.value;
    this.setState({ email: userInput });
    console.log(userInput);
  }

  colocarSenha(e) {
    let senha = e.target.value;
    this.setState({ senha: senha });
    console.log(senha);
  }

  render() {
    return (
      <div className="App-header">
        email:
        <input
          type="email"
          name="email"
          value={this.state.email}
          onChange={this.trocarEmail}
        />
        <br />
        Senha:
        <input
          type="password"
          name="senha"
          value={this.state.senha}
          onChange={this.colocarSenha}
        />
      </div>
    );
  }
}

export default App;
