import './App.css';
import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      form: {
        nome: 'e',
        email: 'eeee',
        senha: 'ewewewe',
        sexo: 'wewewewewewe',
        error: 'asasa',
      },
    };
    this.dadosForm = this.dadosForm.bind(this);
    this.cadastrar = this.cadastrar.bind(this);
  }

  cadastrar(e) {
    e.preventDefault();
    const { nome, email, senha, sexo } = this.state;
    if (nome !== '' && email !== '' && senha !== '') {
      alert(`nome: ${nome}
    email: ${email}
    senha: ${senha}
    sexo: ${sexo}`);
    } else {
      this.setState({ error: 'Ops!!!! Esta faltando algum dado' });
    }
  }

  dadosForm(e) {
    let form = this.state.form;
    form[e.target.name] = e.target.value;
    this.setState({ form: form });
    console.log(this.setState({ form: form }));
  }

  render() {
    return (
      <div className="App-header">
        <h1>Novo Usuario</h1>

        {this.state.error && <p>{this.state.form.error}</p>}

        <form onSubmit={this.cadastrar}>
          <label>Nome - </label>
          <input
            type="text"
            name="nome"
            value={this.state.form.nome}
            onChange={this.dadosForm}
          />
          <br />
          <label>Email - </label>
          <input
            type="email"
            name="email"
            value={this.state.form.email}
            onChange={this.dadosForm}
          />
          <br />
          <label>Senha - </label>
          <input
            type="password"
            name="password"
            value={this.state.form.senha}
            onChange={this.dadosForm}
          />
          <br />
          Sexo:
          <select
            name="sexo"
            value={this.state.form.sexo}
            onChange={this.dadosForm}
          >
            <option value="masculino">Masculino</option>
            <option value="feminino">Feminino</option>
          </select>
          <br />
          <button type="submit">Cadastrar</button>
        </form>
        <div>
          <h3>{this.state.form.name}</h3>
          <h3>{this.state.form.email}</h3>
          <h3>{this.state.form.senha}</h3>
          <h3>{this.state.form.sexo}</h3>
        </div>
      </div>
    );
  }
}

export default App;
