import React, { Component } from 'react';
// import Button from './components/Button';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.satate = {
      sentence: 'sentencesentence',
    };

    this.openCookie = this.openCookie.bind(this);

    this.sentence2 = [
      'Siga os bons e aprenda com eles.',
      'O bom-senso vale mais do que muito conhecimento.',
      'O riso é a menor distância entre duas pessoas.',
      'Deixe de lado as preocupações e seja feliz.',
      'Realize o óbvio, pense no improvável e conquiste o impossível.',
      'Acredite em milagres, mas não dependa deles.',
      'A maior barreira para o sucesso é o medo do fracasso.',
    ];
  }

  openCookie() {
    let state = this.state;
    let number = Math.floor(Math.random() * this.sentence2.length);
    state.txt = '" ' + this.sentence2[number] + ' "';
    this.setState(state);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <img src={require('./assets/biscoito.png')} alt="" />
            <Button name="Open Cookie" openBtn={this.openCookie} />
            <h1>k {this.props.state.sentence}</h1>
          </div>
        </header>
      </div>
    );
  }
}

class Button extends Component {
  render() {
    return (
      <div>
        <button onClick={this.props.openBtn}>{this.props.name}</button>
      </div>
    );
  }
}

export default App;
